sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   //Only allow script to be submitted if short_description field is populated
	var sdField = "$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$ This is the short description: " +  g_form.getValue('short_description');
	jslog(sdField);

	//Don't allow the record to be submitted if short description is not populated
	if(g_form.getValue('short_description') == ''){
		jslog("$$$$$$$$$$$$$$$$$ in the if");
		g_form.hideFieldMsg('short_description',true);
		g_form.showFieldMsg('short_description', 'The Short Description Field cannot be left empty', 'error');
		return false;
	}
}
