sysauto_script.script:
//Get todays date
var rightNow = new GlideDateTime();

//Query the databae for tasks that are overdue
//Only return querys that do not have state field value of closed complete
var grOverdue = new GlideRecord('x_58872_needit_needit_task');
grOverdue.addQuery('due_date','<=',rightNow);
grOverdue.addQuery('state','!=',3);

grOverdue.query();

//Write log message for each overdue task
while(grOverdue.next()){
	gs.info('This NeedIt task is over due ' + grOverdue.number);
	gs.eventQueue('x_58872_needit.overdueNeedItTask',grOverdue,grOverdue.number,gs.getUserName());
}
